mapper.select:
    	select A.place, A.count
		from (
			select place, IFNULL(sum(count), 0) count
			from wine.wine_log
			where user_id = #{userId} and type = 'IN'
			group by place
			order by count desc
			limit 10
		) A

		union all

		select 'others' as place, IFNULL(sum(B.count), 0) count
		from (
			select ROW_NUMBER() OVER() row_num, A.place, A.count
			from(
				select place, IFNULL(sum(count), 0) count
				from wine.wine_log
				where user_id = #{userId} and type = 'IN'
				group by place
				order by count desc
			) A
		) B
		where B.row_num > 10;

mapper.update:
update
	wine.member
set
	language = #{language}
where
	user_id = #{userId}
