mapper.select:
select count(*) from wine.wine
user_id = #{userId}
and wine_type = #{type}
and count > 0

select W.wine_id, W.user_id, W.name, W.link, W.thumb, W.thumb_bottom, W.country, W.region, W.rating, W.average_rating, W.vintage, W.count, W.size, W.wine_type, W.alcohol, W.grape, W.winery , avg(L.price) average_price , (select max(date) from wine.wine_log where wine_id = w.wine_id and type = 'IN') last_buy_date from wine.wine W left join wine.wine_log L on L.wine_id = W.wine_id
W.user_id = #{userId}
and W.wine_type = #{type}
and W.count > 0
group by W.wine_id order by
last_buy_date asc, last_buy_date desc, W.name asc, W.name desc, average_price asc, average_price desc, W.count asc, W.count desc,
W.wine_id desc limit #{pagination.limitStart}, #{recordSize}

mapper.update:
update
	wine.wine_log
set
	date = #{date}, place = #{place}, price = #{price}, count = #{count}, note = #{note}
where
	log_id = #{logId}
	and 
	user_id = #{userId}

update
	wine.review
set
	rating = #{rating}, title = #{title}, photo = #{photo}, content = #{content}
where
	review_id = #{reviewId} and user_id = #{userId}
